gals = []

lines = s.splitlines()
has_gal_in_row = [False for _ in range(len(lines))]
has_gal_in_col = [False for _ in range(len(lines[0]))]
gals = []
for row_i, line in enumerate(lines):
    col_i = 0
    while True:
        try:
            col_i = line.index('#', col_i)
            gals.append((row_i, col_i))
            has_gal_in_row[row_i] = True
            has_gal_in_col[col_i] = True
            col_i = col_i + 1
        except ValueError:
            break

total = 0
for i1, gal1 in enumerate(gals):
    for i2 in range(i1 + 1, len(gals)):
        gal2 = gals[i2]
        r1, c1 = gal1[0], gal1[1]
        r2, c2 = gal2[0], gal2[1]

        d_r = abs(r2 - r1)
        count_r = 0
        for i in range(r1, r2 + 1):
            if not has_gal_in_row[i]:
                count_r = count_r + 1

        count_r = count_r * 999999  # Part2 tweak
        d_r = d_r + count_r

        d_c = abs(c2 - c1)
        if c2 < c1:
            c1, c2 = c2, c1
        count_c = 0
        for i in range(c1, c2 + 1):
            if not has_gal_in_col[i]:
                count_c = count_c + 1

        count_c = count_c * 999999  # Part2 tweak
        d_c = d_c + count_c

        d = d_r + d_c
        total = total + d

print(total)  # 9947476, 519939907614
